targets.expr:
avg(
  # The amount of inflight Kademlia queries per node.
  sum by (instance) (
    # The total amount of Kademlia GET_VALUE queries started.
    ${metric_namespace}_authority_discovery_authority_addresses_requested_total{instance=~"${nodename}"}
  
    # The total amount of Kademlia PUT_VALUE queries started.
    + ${metric_namespace}_authority_discovery_times_published_total{instance=~"${nodename}"}
  )
  - sum by (instance) (
    # The total amount of Kademlia queries (both GET_VALUE and PUT_VALUE) finished.
    ${metric_namespace}_authority_discovery_dht_event_received{instance=~"${nodename}"}
  )
)
